/**
 * This is the default file which will be imported for routes.
 * We can add multiple routes if required and exports using routes as array
 */

import { Route } from '../interfaces/route';
import * as route from "./product";

export const routes: Array<Route> = [
    route.addProduct,
    route.getProduct,
    route.getMostViewedProducts,
    route.deleteProduct
];

/**
 * All Product related routes are exported from here
 */
import { Route } from "../interfaces/route";
import * as payloadValidation from "./../schema/product";
import * as productController from "./../controllers/product";

/**
 * Add Product route
 */
export const addProduct: Route = {
    method: "POST",
    path: "/addProduct",
    config: {
        auth: false,
        handler: productController.addProduct,
        description: "Add Product",
        validate: payloadValidation.addProduct
    }
};

/**
 * Get Product route
 */
export const getProduct: Route = {
    method: "GET",
    path: "/getProduct/{id}/{currency?}",
    config: {
        auth: false,
        handler: productController.getProduct,
        description: "Get a Single Product",
        validate: payloadValidation.getProduct
    }
};

/**
 * Get Most Viewed Product route
 */
export const getMostViewedProducts: Route = {
    method: "GET",
    path: "/getMostViewedProducts/{total}/{currency?}",
    config: {
        auth: false,
        handler: productController.getMostViewedProducts,
        description: "Get Most Viewed Products",
        validate: payloadValidation.getMostViewedProducts
    }
};

/**
 * Delete Product Route
 */
export const deleteProduct: Route = {
    method: "DELETE",
    path: "/deleteProduct",
    config: {
        auth: false,
        handler: productController.deleteProduct,
        description: "Delete Product",
        validate: payloadValidation.deleteProduct
    }
};

import Joi from "joi";

/**
 * Validation scehema for Add Product Route using Joi
 */
export const addProduct = {
    payload: Joi.object(
        {
            name: Joi
                .string()
                .strip()
                .required()
                .description("Name of the Product"),
            price: Joi
                .number()
                .greater(0)
                .strip()
                .required()
                .description("Price in USD upto two decimal points"),
            description: Joi
                .string()
                .strip()
                .optional()
                .description("Description of product"),
        }
    )
};

/**
 * Validation scehema for Get Product Route using Joi
 */
export const getProduct = {
    params: Joi.object(
        {
            id: Joi
                .number()
                .strip()
                .required()
                .description("Name of the Product"),
            currency: Joi
                .string()
                .strip()
                .optional()
                .valid("USD","CAD","EUR","GBP")
                .description("Currency in which you want to see the product")
        }
    )
};

/**
 * Validation scehema for Get Most Viewed Products Route using Joi
 */
export const getMostViewedProducts = {
    params: Joi.object(
        {
            total: Joi
                .number()
                .strip()
                .optional()
                .default(5)
                .description("Number of products"),
            currency: Joi
                .string()
                .strip()
                .optional()
                .valid("USD","CAD","EUR","GBP")
                .description("Currency in which you want to see the product")                    
        }
    )
};

/**
 * Validation scehema for Delete Product Route using Joi
 */
export const deleteProduct = {
    payload: Joi.object(
        {
            id: Joi
                .number()
                .strip()
                .required()
                .description("Product Id"),
        }
    )
};

import Hapi from "@hapi/hapi";
import { Server } from "@hapi/hapi";
import { exit } from "process";
import {routes} from "./routes";

export let server: Server;

/**
 * Function to initilize Hapi Server and adding routes to it.
 * @returns {Promise<Server>}
 */
export const init = async (): Promise<Server> => {
    server = Hapi.server({
        port: process.env.PORT || 7700,
        host: 'localhost'
    });
   
    server.route(routes); 

    return server;
};

/**
 * Function to start Hapi Server.
 * @returns {void}
 */
export const start = async (): Promise<void> => {
    console.log(`Listening on ${server.settings.host}:${server.settings.port}`);
    return server.start();
};

process.on('unhandledRejection', (err) => {
    console.error("unhandledRejection");
    console.error(err);
    process.exit(1);
});

import { beforeEach,afterEach,describe,it } from 'mocha';
import { init } from "./../server";
import { Server } from "@hapi/hapi";
import { expect } from "chai"

/**
 * Test for Get Product API
 */
describe('Get Products API Testing', ()=>{
    let server:Server;

    beforeEach(async ()=>{
        server = await init();
    });

    afterEach(async ()=>{
        await server.stop();
    });

    it('getProducts Without Currency', async () => {
        const res = await server.inject({
            method: 'get',
            url: '/getProduct/1'
        });
        expect(res.statusCode).to.equal(200);
    });

    it('getProducts With Valid Currency', async () => {
        const res = await server.inject({
            method: 'get',
            url: '/getProduct/1/USD'
        });
        expect(res.statusCode).to.equal(200);
    });

    it('getProducts With Wrong Currency', async () => {
        const res = await server.inject({
            method: 'get',
            url: '/getProduct/1/INR'
        });
        expect(res.statusCode).to.equal(400);
    });
});

/**
 * Test for Add Product API
 */
describe('Add Products API Testing', ()=>{
    let server:Server;

    beforeEach(async ()=>{
        server = await init();
    });

    afterEach(async ()=>{
        await server.stop();
    });

    it('addProducts without description', async () => {
        const res = await server.inject({
            method: 'post',
            url: '/addProduct',
            payload: { name: "CBR 650F", price: 1.34 },
        });
        expect(res.statusCode).to.equal(200);
    });

    it('addProducts with description', async () => {
        const res = await server.inject({
            method: 'post',
            url: '/addProduct',
            payload: { name: "CBR 650F", price: 1.34, description: "test description" },
        });
        expect(res.statusCode).to.equal(200);
    });

    it('addProducts price should be greater than 0', async () => {
        const res = await server.inject({
            method: 'post',
            url: '/addProduct',
            payload: { name: "CBR 650F", price: 0 },
        });
        expect(res.statusCode).to.equal(400);
    });

    it('addProducts price is required', async () => {
        const res = await server.inject({
            method: 'post',
            url: '/addProduct',
            payload: { name: "CBR 650F" },
        });
        expect(res.statusCode).to.equal(400);
    });

    it('addProducts name is required', async () => {
        const res = await server.inject({
            method: 'post',
            url: '/addProduct',
            payload: { price: 1 },
        });
        expect(res.statusCode).to.equal(400);
    });
});
